		<script type="text/javascript">
			function _gzip_check()
			{
				if(document.cookie.match('GUNZIP=1'))
					return;
				
				var links = document.getElementsByTagName('LINK');
				for(var i = 0; i < links.length; i++)
					if(links[i].rel === 'stylesheet'
					&& (!links[i].media || links[i].media === 'all')
					&& (links[i].href.indexOf('/') === 0 || links[i].href.indexOf(location.protocol + '//' + location.hostname + '/') === 0))
					{
						var styleSheet = links[i].styleSheet || links[i].sheet;
						var rules = styleSheet ? (styleSheet.rules || styleSheet.cssRules) : null;
						if(!styleSheet || rules.length === 0)
						{
							var date = new Date();
							date.setYear(date.getFullYear() + 1);
							document.cookie = 'GUNZIP=1; expires=' + date.toGMTString() + '; path=/; domain=' + location.hostname;
							location.reload(true);
							return;
						}
					}
			}
		</script>
